import React, { useState } from "react";
import TextArea from "./textarea";

const Note = () => {
  const [text, setText] = useState([
    {
      content: " "
    }
  ]);

  const TextContent = ({ cont, index, deleteNote }) => {
    return (
      <div className='row '>
        <div className='col s12 m6 note-card'>
          <div className='card  darken-1'>
            <div className='card-content black-text'>
              <p>{cont.content}</p>
            </div>
            <div className='card-action'>
              {/* Modal Trigger */}
              <a className='modal-trigger edits' href='#modal1'>
                Edit
              </a>

              <a href='#' className='edits' onClick={() => deleteNote(index)}>
                Delete
              </a>
            </div>
          </div>
        </div>
      </div>
    );
  };

  /* Textarea */

  const addNote = content => {
    const newNote = [...text];
    newNote.unshift({ content });
    setText(newNote);
  };

  const deleteNote = index => {
    const newNote = [...text];
    newNote.splice(index, 1);
    setText(newNote);
  };

  return (
    <div>
      <TextArea addNote={addNote} />
      {text.map((cont, index) => (
        <TextContent
          key={index}
          index={index}
          cont={cont}
          deleteNote={deleteNote}
        />
      ))}

      {/* Modal Structure */}
      <div id='modal1' className='modal'>
        <div className='modal-content'>
          <span className='card-title'>Make an Edit</span>
          <form class='col s12 center'>
            <div class='row'>
              <div className='input-field col s12'>
                <textarea
                  id='textarea2'
                  className='materialize-textarea'
                  data-length='120'
                />
                <label for='textarea2'>Textarea</label>
              </div>
            </div>

            <button
              className='btn waves-effect waves-light save-btn'
              type='submit'
              name='action'
            >
              Save
            </button>
          </form>
        </div>
        <div className='modal-footer'>
          <a
            href='#!'
            className='modal-close waves-effect waves-green btn-flat'
          >
            Agree
          </a>
        </div>
      </div>
    </div>
  );
};

export default Note;
